    );
    AdminUpgradeabilityProxy gsmProxy = new AdminUpgradeabilityProxy(
      address(gsm),
      SHORT_EXECUTOR,
      ''
    );
    GHO_GSM = Gsm(address(gsmProxy));

    GHO_GSM.initialize(address(this), TREASURY, DEFAULT_GSM_USDC_EXPOSURE);
    GHO_GSM_4626 = new Gsm4626(
      address(GHO_TOKEN),
      address(USDC_4626_TOKEN),
      address(GHO_GSM_4626_FIXED_PRICE_STRATEGY)
    );
    GHO_GSM_4626.initialize(address(this), TREASURY, DEFAULT_GSM_USDC_EXPOSURE);

    Gsm buidlGsm = new Gsm(
      address(GHO_TOKEN),
      address(BUIDL_TOKEN),
      address(GHO_BUIDL_GSM_FIXED_PRICE_STRATEGY)
    );
    AdminUpgradeabilityProxy buidlGsmProxy = new AdminUpgradeabilityProxy(
      address(buidlGsm),
      SHORT_EXECUTOR,
      ''
    );
    GHO_BUIDL_GSM = Gsm(address(buidlGsmProxy));
    GHO_BUIDL_GSM.initialize(address(this), TREASURY, DEFAULT_GSM_BUIDL_EXPOSURE);

    GHO_GSM_FIXED_FEE_STRATEGY = new FixedFeeStrategy(DEFAULT_GSM_BUY_FEE, DEFAULT_GSM_SELL_FEE);
    GHO_GSM.updateFeeStrategy(address(GHO_GSM_FIXED_FEE_STRATEGY));
    GHO_GSM_4626.updateFeeStrategy(address(GHO_GSM_FIXED_FEE_STRATEGY));
    GHO_BUIDL_GSM.updateFeeStrategy(address(GHO_GSM_FIXED_FEE_STRATEGY));

    GHO_GSM.grantRole(GSM_LIQUIDATOR_ROLE, address(GHO_GSM_LAST_RESORT_LIQUIDATOR));
    GHO_GSM.grantRole(GSM_SWAP_FREEZER_ROLE, address(GHO_GSM_SWAP_FREEZER));
    GHO_GSM_4626.grantRole(GSM_LIQUIDATOR_ROLE, address(GHO_GSM_LAST_RESORT_LIQUIDATOR));
    GHO_GSM_4626.grantRole(GSM_SWAP_FREEZER_ROLE, address(GHO_GSM_SWAP_FREEZER));

    GHO_TOKEN.addFacilitator(address(GHO_GSM), 'GSM Facilitator', DEFAULT_CAPACITY);
    GHO_TOKEN.addFacilitator(address(GHO_GSM_4626), 'GSM 4626 Facilitator', DEFAULT_CAPACITY);
    GHO_TOKEN.addFacilitator(FAUCET, 'Faucet Facilitator', type(uint128).max);
    GHO_TOKEN.addFacilitator(address(GHO_BUIDL_GSM), 'GSM BUIDL Facilitator', DEFAULT_CAPACITY);

    GHO_GSM_REGISTRY = new GsmRegistry(address(this));
    FIXED_RATE_STRATEGY_FACTORY = new FixedRateStrategyFactory(address(PROVIDER));

    // Deploy Gho Token Pool
    address ARM_PROXY = makeAddr('ARM_PROXY');
    address OWNER = makeAddr('OWNER');
    address ROUTER = makeAddr('ROUTER');
    address PROXY_ADMIN = makeAddr('PROXY_ADMIN');
    uint256 INITIAL_BRIDGE_LIMIT = 100e6 * 1e18;
    MockUpgradeableLockReleaseTokenPool tokenPoolImpl = new MockUpgradeableLockReleaseTokenPool(
      address(GHO_TOKEN),
      ARM_PROXY,
      false,
      true
    );
    // proxy deploy and init
    address[] memory emptyArray = new address[](0);
    bytes memory tokenPoolInitParams = abi.encodeWithSignature(
      'initialize(address,address[],address,uint256)',
      OWNER,
      emptyArray,
      ROUTER,
      INITIAL_BRIDGE_LIMIT
    );
    TransparentUpgradeableProxy tokenPoolProxy = new TransparentUpgradeableProxy(
      address(tokenPoolImpl),
      PROXY_ADMIN,
      tokenPoolInitParams
    );

    // Manage ownership
    vm.prank(OWNER);
    MockUpgradeableLockReleaseTokenPool(address(tokenPoolProxy)).acceptOwnership();
    GHO_TOKEN_POOL = MockUpgradeableLockReleaseTokenPool(address(tokenPoolProxy));

    // Setup GHO Token Pool
    uint64 SOURCE_CHAIN_SELECTOR = 1;
    uint64 DEST_CHAIN_SELECTOR = 2;
    RateLimiter.Config memory initialOutboundRateLimit = RateLimiter.Config({
      isEnabled: true,
      capacity: 100e28,
      rate: 1e15
    });
    RateLimiter.Config memory initialInboundRateLimit = RateLimiter.Config({
      isEnabled: true,
      capacity: 222e30,
      rate: 1e18
    });
    MockUpgradeableLockReleaseTokenPool.ChainUpdate[]
      memory chainUpdate = new MockUpgradeableLockReleaseTokenPool.ChainUpdate[](1);
    chainUpdate[0] = MockUpgradeableLockReleaseTokenPool.ChainUpdate({
      remoteChainSelector: DEST_CHAIN_SELECTOR,
      allowed: true,
      outboundRateLimiterConfig: initialOutboundRateLimit,
      inboundRateLimiterConfig: initialInboundRateLimit
    });
    vm.prank(OWNER);
    GHO_TOKEN_POOL.applyChainUpdates(chainUpdate);

    BUIDL_USDC_REDEMPTION = new MockRedemption(address(BUIDL_TOKEN), address(USDC_TOKEN));
    BUIDL_USDC_REDEMPTION_FAILED_ISSUED_ASSET_AMOUNT = new MockRedemptionFailedIssuedAssetAmount(
      address(BUIDL_TOKEN),
      address(USDC_TOKEN)
    );
    BUIDL_USDC_REDEMPTION_FAILED = new MockRedemptionFailed(
      address(BUIDL_TOKEN),
      address(USDC_TOKEN)
    );
    BUIDL_USDC_ISSUANCE = new MockBUIDLSubscription(address(BUIDL_TOKEN), address(USDC_TOKEN));
    BUIDL_USDC_ISSUANCE_FAILED = new MockBUIDLSubscriptionFailed(
      address(BUIDL_TOKEN),
      address(USDC_TOKEN)
    );
    BUIDL_USDC_ISSUANCE_FAILED_INVALID_USDC = new MockBUIDLSubscriptionFailedInvalidUSDCAccepted(
      address(BUIDL_TOKEN),
      address(USDC_TOKEN)
    );
    GSM_CONVERTER = new GsmConverter(
      address(this),
      address(GHO_BUIDL_GSM),
      address(BUIDL_USDC_REDEMPTION),
      address(BUIDL_USDC_ISSUANCE),
      address(BUIDL_TOKEN),
      address(USDC_TOKEN)
    );
  }

  function ghoFaucet(address to, uint256 amount) public {
    vm.prank(FAUCET);
    GHO_TOKEN.mint(to, amount);
  }

  function borrowAction(address user, uint256 amount) public {
    borrowActionOnBehalf(user, user, amount);
  }

  function borrowActionOnBehalf(address caller, address onBehalfOf, uint256 amount) public {
    BorrowState memory bs;
    bs.supplyBeforeAction = GHO_TOKEN.totalSupply();
    bs.debtSupplyBeforeAction = GHO_DEBT_TOKEN.totalSupply();
    bs.debtScaledSupplyBeforeAction = GHO_DEBT_TOKEN.scaledTotalSupply();
    bs.balanceBeforeAction = GHO_TOKEN.balanceOf(onBehalfOf);
    bs.debtScaledBalanceBeforeAction = GHO_DEBT_TOKEN.scaledBalanceOf(onBehalfOf);
    bs.debtBalanceBeforeAction = GHO_DEBT_TOKEN.balanceOf(onBehalfOf);
    bs.userIndexBeforeAction = GHO_DEBT_TOKEN.getPreviousIndex(onBehalfOf);
    bs.userInterestsBeforeAction = GHO_DEBT_TOKEN.getBalanceFromInterest(onBehalfOf);
    bs.assetIndexBefore = POOL.getReserveNormalizedVariableDebt(address(GHO_TOKEN));
    bs.discountPercent = GHO_DEBT_TOKEN.getDiscountPercent(onBehalfOf);

    if (bs.userIndexBeforeAction == 0) {
      bs.userIndexBeforeAction = 1e27;
    }

    (uint256 computedInterest, uint256 discountScaled, ) = DebtUtils.computeDebt(
      bs.userIndexBeforeAction,
      bs.assetIndexBefore,
      bs.debtScaledBalanceBeforeAction,
      bs.userInterestsBeforeAction,
      bs.discountPercent
    );
    uint256 newDiscountRate = GHO_DISCOUNT_STRATEGY.calculateDiscountRate(
      (bs.debtScaledBalanceBeforeAction - discountScaled).rayMul(bs.assetIndexBefore) + amount,
      IERC20(address(STK_TOKEN)).balanceOf(onBehalfOf)
    );

    if (newDiscountRate != bs.discountPercent) {
      vm.expectEmit(true, true, true, true, address(GHO_DEBT_TOKEN));
      emit DiscountPercentUpdated(onBehalfOf, bs.discountPercent, newDiscountRate);
    }

    vm.expectEmit(true, true, true, true, address(GHO_DEBT_TOKEN));
    emit Transfer(address(0), onBehalfOf, amount + computedInterest);
    vm.expectEmit(true, true, true, true, address(GHO_DEBT_TOKEN));
    emit Mint(caller, onBehalfOf, amount + computedInterest, computedInterest, bs.assetIndexBefore);

    // Action
    vm.prank(caller);
    POOL.borrow(address(GHO_TOKEN), amount, 2, 0, onBehalfOf);

    // Checks
    assertEq(
      GHO_TOKEN.balanceOf(onBehalfOf),
      bs.balanceBeforeAction + amount,
      'Gho amount does not match borrow'
    );
    assertEq(GHO_DEBT_TOKEN.getDiscountPercent(onBehalfOf), newDiscountRate);
    assertEq(
      GHO_TOKEN.totalSupply(),
      bs.supplyBeforeAction + amount,
      'Gho total supply does not match borrow'
    );

    assertEq(
      GHO_DEBT_TOKEN.scaledBalanceOf(onBehalfOf),
      bs.debtScaledBalanceBeforeAction + amount.rayDiv(bs.assetIndexBefore) - discountScaled,
      'Gho debt token balance does not match borrow'
    );
    assertEq(
      GHO_DEBT_TOKEN.scaledTotalSupply(),
      bs.debtScaledSupplyBeforeAction + amount.rayDiv(bs.assetIndexBefore) - discountScaled,
      'Gho debt token Supply does not match borrow'
    );
    assertEq(
      GHO_DEBT_TOKEN.getBalanceFromInterest(onBehalfOf),
      bs.userInterestsBeforeAction + computedInterest,
      'Gho debt interests does not match borrow'
    );
  }

  function repayAction(address user, uint256 amount) public {
    BorrowState memory bs;
    bs.supplyBeforeAction = GHO_TOKEN.totalSupply();
    bs.debtSupplyBeforeAction = GHO_DEBT_TOKEN.totalSupply();
    bs.debtScaledSupplyBeforeAction = GHO_DEBT_TOKEN.scaledTotalSupply();
    bs.balanceBeforeAction = GHO_TOKEN.balanceOf(user);
    bs.debtScaledBalanceBeforeAction = GHO_DEBT_TOKEN.scaledBalanceOf(user);
    bs.debtBalanceBeforeAction = GHO_DEBT_TOKEN.balanceOf(user);
    bs.userIndexBeforeAction = GHO_DEBT_TOKEN.getPreviousIndex(user);
    bs.userInterestsBeforeAction = GHO_DEBT_TOKEN.getBalanceFromInterest(user);
    bs.assetIndexBefore = POOL.getReserveNormalizedVariableDebt(address(GHO_TOKEN));
    bs.discountPercent = GHO_DEBT_TOKEN.getDiscountPercent(user);
    uint256 expectedDebt = 0;
    uint256 expectedBurnOffset = 0;

    if (bs.userIndexBeforeAction == 0) {
      bs.userIndexBeforeAction = 1e27;
    }

    (uint256 computedInterest, uint256 discountScaled, ) = DebtUtils.computeDebt(
      bs.userIndexBeforeAction,
      bs.assetIndexBefore,
      bs.debtScaledBalanceBeforeAction,
      bs.userInterestsBeforeAction,
      bs.discountPercent
    );
    uint256 newDiscountRate = GHO_DISCOUNT_STRATEGY.calculateDiscountRate(
      (bs.debtScaledBalanceBeforeAction - discountScaled).rayMul(bs.assetIndexBefore) - amount,
      IERC20(address(STK_TOKEN)).balanceOf(user)
    );

    if (amount <= (bs.userInterestsBeforeAction + computedInterest)) {
      expectedDebt = bs.userInterestsBeforeAction + computedInterest - amount;
    } else {
      expectedBurnOffset = amount - bs.userInterestsBeforeAction + computedInterest;
    }

    // Action
    vm.startPrank(user);
    GHO_TOKEN.approve(address(POOL), amount);

    if (newDiscountRate != bs.discountPercent) {
      vm.expectEmit(true, true, true, true, address(GHO_DEBT_TOKEN));
      emit DiscountPercentUpdated(user, bs.discountPercent, newDiscountRate);
    }

    if (computedInterest > amount) {
      vm.expectEmit(true, true, true, true, address(GHO_DEBT_TOKEN));
      emit Transfer(address(0), user, computedInterest - amount);
    } else {
      vm.expectEmit(true, true, true, true, address(GHO_DEBT_TOKEN));
      emit Transfer(user, address(0), amount - computedInterest);
    }

    POOL.repay(address(GHO_TOKEN), amount, 2, user);
    vm.stopPrank();

    // Checks
    assertEq(
      GHO_TOKEN.balanceOf(user),
      bs.balanceBeforeAction - amount,
      'Gho amount does not match repay'
    );
    assertEq(GHO_DEBT_TOKEN.getDiscountPercent(user), newDiscountRate);
    if (expectedBurnOffset != 0) {
      assertEq(
        GHO_TOKEN.totalSupply(),
        bs.supplyBeforeAction - amount + computedInterest + bs.userInterestsBeforeAction,
        'Gho total supply does not match repay b'
      );
    } else {
      assertEq(
        GHO_TOKEN.totalSupply(),
        bs.supplyBeforeAction,
        'Gho total supply does not match repay a'
      );
    }

    assertEq(
      GHO_DEBT_TOKEN.scaledBalanceOf(user),
      bs.debtScaledBalanceBeforeAction - amount.rayDiv(bs.assetIndexBefore) - discountScaled,
      'Gho debt token balance does not match repay'
    );
    assertEq(
      GHO_DEBT_TOKEN.scaledTotalSupply(),
      bs.debtScaledSupplyBeforeAction - amount.rayDiv(bs.assetIndexBefore) - discountScaled,
      'Gho debt token Supply does not match repay'
    );
    assertEq(
      GHO_DEBT_TOKEN.getBalanceFromInterest(user),
      expectedDebt,
      'Gho debt interests does not match repay'
    );
  }

  function mintAndStakeDiscountToken(address user, uint256 amount) public {
    vm.prank(FAUCET);
    AAVE_TOKEN.mint(user, amount);

    vm.startPrank(user);
    AAVE_TOKEN.approve(address(STK_TOKEN), amount);
    STK_TOKEN.stake(user, amount);
    vm.stopPrank();
  }

  function rebalanceDiscountAction(address user) public {
    BorrowState memory bs;
    bs.supplyBeforeAction = GHO_TOKEN.totalSupply();
    bs.debtSupplyBeforeAction = GHO_DEBT_TOKEN.totalSupply();
    bs.debtScaledSupplyBeforeAction = GHO_DEBT_TOKEN.scaledTotalSupply();
    bs.balanceBeforeAction = GHO_TOKEN.balanceOf(user);
    bs.debtScaledBalanceBeforeAction = GHO_DEBT_TOKEN.scaledBalanceOf(user);
    bs.debtBalanceBeforeAction = GHO_DEBT_TOKEN.balanceOf(user);
    bs.userIndexBeforeAction = GHO_DEBT_TOKEN.getPreviousIndex(user);
    bs.userInterestsBeforeAction = GHO_DEBT_TOKEN.getBalanceFromInterest(user);
    bs.assetIndexBefore = POOL.getReserveNormalizedVariableDebt(address(GHO_TOKEN));
    bs.discountPercent = GHO_DEBT_TOKEN.getDiscountPercent(user);

    if (bs.userIndexBeforeAction == 0) {
      bs.userIndexBeforeAction = 1e27;
    }

    (uint256 computedInterest, uint256 discountScaled, ) = DebtUtils.computeDebt(
      bs.userIndexBeforeAction,
      bs.assetIndexBefore,
      bs.debtScaledBalanceBeforeAction,
      bs.userInterestsBeforeAction,
      bs.discountPercent
    );
    uint256 newDiscountRate = GHO_DISCOUNT_STRATEGY.calculateDiscountRate(
      (bs.debtScaledBalanceBeforeAction - discountScaled).rayMul(bs.assetIndexBefore),
      IERC20(address(STK_TOKEN)).balanceOf(user)
    );

    if (newDiscountRate != bs.discountPercent) {
      vm.expectEmit(true, true, true, true, address(GHO_DEBT_TOKEN));
      emit DiscountPercentUpdated(user, bs.discountPercent, newDiscountRate);
    }

    vm.expectEmit(true, true, true, true, address(GHO_DEBT_TOKEN));
    emit Transfer(address(0), user, computedInterest);
    vm.expectEmit(true, true, true, true, address(GHO_DEBT_TOKEN));
    emit Mint(address(0), user, computedInterest, computedInterest, bs.assetIndexBefore);

    // Action
    vm.prank(user);
    GHO_DEBT_TOKEN.rebalanceUserDiscountPercent(user);

    // Checks
    assertEq(
      GHO_TOKEN.balanceOf(user),
      bs.balanceBeforeAction,
      'Gho amount does not match rebalance'
    );
    assertEq(GHO_DEBT_TOKEN.getDiscountPercent(user), newDiscountRate);
    assertEq(
      GHO_TOKEN.totalSupply(),
      bs.supplyBeforeAction,
      'Gho total supply does not match rebalance'
    );

    assertEq(
      GHO_DEBT_TOKEN.scaledBalanceOf(user),
      bs.debtScaledBalanceBeforeAction - discountScaled,
      'Gho debt token balance does not match rebalance'
    );
    assertEq(
      GHO_DEBT_TOKEN.scaledTotalSupply(),
      bs.debtScaledSupplyBeforeAction - discountScaled,
      'Gho debt token Supply does not match borrow'
    );
    assertEq(
      GHO_DEBT_TOKEN.getBalanceFromInterest(user),
      bs.userInterestsBeforeAction + computedInterest,
      'Gho debt interests does not match borrow'
    );
  }

  /// Helper function to sell asset in the GSM
  function _sellAsset(
    Gsm gsm,
    TestnetERC20 token,
    address receiver,
    uint256 amount
  ) internal returns (uint256) {
    vm.startPrank(FAUCET);
    token.mint(FAUCET, amount);
    token.approve(address(gsm), amount);
    (, uint256 ghoBought) = gsm.sellAsset(amount, receiver);
    vm.stopPrank();
    return ghoBought;
  }

  /// Helper function to mint an amount of assets of an ERC4626 token
  function _mintVaultAssets(
    MockERC4626 vault,
    TestnetERC20 token,
    address receiver,
    uint256 amount
  ) internal {
    vm.startPrank(FAUCET);
    token.mint(FAUCET, amount);
    token.approve(address(vault), amount);
    vault.deposit(amount, receiver);
    vm.stopPrank();
  }

  /// Helper function to mint an amount of shares of an ERC4626 token
  function _mintVaultShares(
    MockERC4626 vault,
    TestnetERC20 token,
    address receiver,
    uint256 sharesAmount
  ) internal {
    uint256 assets = vault.previewMint(sharesAmount);
    vm.startPrank(FAUCET);
    token.mint(FAUCET, assets);
    token.approve(address(vault), assets);
    vault.deposit(assets, receiver);
    vm.stopPrank();
  }

  /// Helper function to sell shares of an ERC4626 token in the GSM
  function _sellAsset(
    Gsm4626 gsm,
    MockERC4626 vault,
    TestnetERC20 token,
    address receiver,
    uint256 amount
  ) internal returns (uint256) {
    uint256 assetsToMint = vault.previewRedeem(amount);
    _mintVaultAssets(vault, token, address(this), assetsToMint);
    vault.approve(address(gsm), amount);
    (, uint256 ghoBought) = gsm.sellAsset(amount, receiver);
    return ghoBought;
  }

  /// Helper function to alter the exchange rate between shares and assets in a ERC4626 vault
  function _changeExchangeRate(
    MockERC4626 vault,
    TestnetERC20 token,
    uint256 amount,
    bool inflate
  ) internal {
    if (inflate) {
      // Inflate
      vm.prank(FAUCET);
      token.mint(address(vault), amount);
    } else {
      // Deflate
      vm.prank(address(vault));
      token.transfer(address(1), amount);
    }
  }

  function _contains(address[] memory list, address item) internal pure returns (bool) {
    for (uint256 i = 0; i < list.length; i++) {
      if (list[i] == item) {
        return true;
      }
    }
    return false;
  }

  function getProxyAdminAddress(address proxy) internal view returns (address) {
    bytes32 adminSlot = vm.load(proxy, ERC1967_ADMIN_SLOT);
    return address(uint160(uint256(adminSlot)));
  }

  function getProxyImplementationAddress(address proxy) internal view returns (address) {
    bytes32 implSlot = vm.load(proxy, ERC1967_IMPLEMENTATION_SLOT);
    return address(uint160(uint256(implSlot)));
  }
}

src/test/TestGhoBucketSteward.t.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import './TestGhoBase.t.sol';

contract TestGhoBucketSteward is TestGhoBase {
  function setUp() public {
    // Deploy Gho Bucket Steward
    GHO_BUCKET_STEWARD = new GhoBucketSteward(SHORT_EXECUTOR, address(GHO_TOKEN), RISK_COUNCIL);
    address[] memory controlledFacilitators = new address[](2);
    controlledFacilitators[0] = address(GHO_ATOKEN);
    controlledFacilitators[1] = address(GHO_GSM);
    vm.prank(SHORT_EXECUTOR);
    GHO_BUCKET_STEWARD.setControlledFacilitator(controlledFacilitators, true);

    /// @dev Since block.timestamp starts at 0 this is a necessary condition (block.timestamp > `MINIMUM_DELAY`) for the timelocked contract methods to work.
    vm.warp(GHO_BUCKET_STEWARD.MINIMUM_DELAY() + 1);

    // Grant roles
    GHO_TOKEN.grantRole(GHO_TOKEN_BUCKET_MANAGER_ROLE, address(GHO_BUCKET_STEWARD));
  }

  function testConstructor() public {
    assertEq(GHO_BUCKET_STEWARD.owner(), SHORT_EXECUTOR);
    assertEq(GHO_BUCKET_STEWARD.GHO_TOKEN(), address(GHO_TOKEN));
    assertEq(GHO_BUCKET_STEWARD.RISK_COUNCIL(), RISK_COUNCIL);

    address[] memory controlledFacilitators = GHO_BUCKET_STEWARD.getControlledFacilitators();
    assertEq(controlledFacilitators.length, 2);

    uint40 facilitatorTimelock = GHO_BUCKET_STEWARD.getFacilitatorBucketCapacityTimelock(
      controlledFacilitators[0]
    );
    assertEq(facilitatorTimelock, 0);
  }

  function testRevertConstructorInvalidOwner() public {
    vm.expectRevert('INVALID_OWNER');
    new GhoBucketSteward(address(0), address(0x002), address(0x003));
  }

  function testRevertConstructorInvalidGhoToken() public {
    vm.expectRevert('INVALID_GHO_TOKEN');
    new GhoBucketSteward(address(0x001), address(0), address(0x003));
  }

  function testRevertConstructorInvalidRiskCouncil() public {
    vm.expectRevert('INVALID_RISK_COUNCIL');
    new GhoBucketSteward(address(0x001), address(0x002), address(0));
  }

  function testChangeOwnership() public {
    address newOwner = makeAddr('newOwner');
    assertEq(GHO_BUCKET_STEWARD.owner(), SHORT_EXECUTOR);
    vm.prank(SHORT_EXECUTOR);
    GHO_BUCKET_STEWARD.transferOwnership(newOwner);
    assertEq(GHO_BUCKET_STEWARD.owner(), newOwner);
  }

  function testChangeOwnershipRevert() public {
    vm.expectRevert('Ownable: new owner is the zero address');
    vm.prank(SHORT_EXECUTOR);
    GHO_BUCKET_STEWARD.transferOwnership(address(0));
  }

  function testUpdateFacilitatorBucketCapacity() public {
    (uint256 currentBucketCapacity, ) = GHO_TOKEN.getFacilitatorBucket(address(GHO_ATOKEN));
    vm.prank(RISK_COUNCIL);
    uint128 newBucketCapacity = uint128(currentBucketCapacity) + 1;
    GHO_BUCKET_STEWARD.updateFacilitatorBucketCapacity(address(GHO_ATOKEN), newBucketCapacity);
    (uint256 capacity, ) = GHO_TOKEN.getFacilitatorBucket(address(GHO_ATOKEN));
    assertEq(newBucketCapacity, capacity);
  }

  function testUpdateFacilitatorBucketCapacityMaxValue() public {
    (uint256 currentBucketCapacity, ) = GHO_TOKEN.getFacilitatorBucket(address(GHO_ATOKEN));
    uint128 newBucketCapacity = uint128(currentBucketCapacity * 2);
    vm.prank(RISK_COUNCIL);
    GHO_BUCKET_STEWARD.updateFacilitatorBucketCapacity(address(GHO_ATOKEN), newBucketCapacity);
    (uint256 capacity, ) = GHO_TOKEN.getFacilitatorBucket(address(GHO_ATOKEN));
    assertEq(capacity, newBucketCapacity);
  }

  function testUpdateFacilitatorBucketCapacityTimelock() public {
    (uint256 currentBucketCapacity, ) = GHO_TOKEN.getFacilitatorBucket(address(GHO_ATOKEN));
    vm.prank(RISK_COUNCIL);
    GHO_BUCKET_STEWARD.updateFacilitatorBucketCapacity(
      address(GHO_ATOKEN),
      uint128(currentBucketCapacity) + 1
    );
    uint40 timelock = GHO_BUCKET_STEWARD.getFacilitatorBucketCapacityTimelock(address(GHO_ATOKEN));
    assertEq(timelock, block.timestamp);
  }

  function testUpdateFacilitatorBucketCapacityAfterTimelock() public {
    (uint256 currentBucketCapacity, ) = GHO_TOKEN.getFacilitatorBucket(address(GHO_ATOKEN));
    vm.prank(RISK_COUNCIL);
    uint128 newBucketCapacity = uint128(currentBucketCapacity) + 1;
    GHO_BUCKET_STEWARD.updateFacilitatorBucketCapacity(address(GHO_ATOKEN), newBucketCapacity);
    skip(GHO_BUCKET_STEWARD.MINIMUM_DELAY() + 1);
    uint128 newBucketCapacityAfterTimelock = newBucketCapacity + 1;
    vm.prank(RISK_COUNCIL);
    GHO_BUCKET_STEWARD.updateFacilitatorBucketCapacity(
      address(GHO_ATOKEN),
      newBucketCapacityAfterTimelock
    );
    (uint256 capacity, ) = GHO_TOKEN.getFacilitatorBucket(address(GHO_ATOKEN));
    assertEq(capacity, newBucketCapacityAfterTimelock);
  }

  function testRevertUpdateFacilitatorBucketCapacityIfUnauthorized() public {
    vm.expectRevert('INVALID_CALLER');
    vm.prank(ALICE);
    GHO_BUCKET_STEWARD.updateFacilitatorBucketCapacity(address(GHO_ATOKEN), 123);
  }

  function testRevertUpdateFacilitatorBucketCapacityIfUpdatedTooSoon() public {
    (uint256 currentBucketCapacity, ) = GHO_TOKEN.getFacilitatorBucket(address(GHO_ATOKEN));
    vm.prank(RISK_COUNCIL);
    GHO_BUCKET_STEWARD.updateFacilitatorBucketCapacity(
      address(GHO_ATOKEN),
      uint128(currentBucketCapacity) + 1
    );
    vm.prank(RISK_COUNCIL);
    vm.expectRevert('DEBOUNCE_NOT_RESPECTED');
    GHO_BUCKET_STEWARD.updateFacilitatorBucketCapacity(
      address(GHO_ATOKEN),
      uint128(currentBucketCapacity) + 2
    );
  }

  function testRevertUpdateFacilitatorBucketCapacityNoChange() public {
    (uint256 currentBucketCapacity, ) = GHO_TOKEN.getFacilitatorBucket(address(GHO_ATOKEN));
    vm.prank(RISK_COUNCIL);
    vm.expectRevert('NO_CHANGE_IN_BUCKET_CAPACITY');
    GHO_BUCKET_STEWARD.updateFacilitatorBucketCapacity(
      address(GHO_ATOKEN),
      uint128(currentBucketCapacity)
    );
  }

  function testRevertUpdateFacilitatorBucketCapacityIfFacilitatorNotInControl() public {
    (uint256 currentBucketCapacity, ) = GHO_TOKEN.getFacilitatorBucket(address(GHO_GSM_4626));
    vm.prank(RISK_COUNCIL);
    vm.expectRevert('FACILITATOR_NOT_CONTROLLED');
    GHO_BUCKET_STEWARD.updateFacilitatorBucketCapacity(
      address(GHO_GSM_4626),
      uint128(currentBucketCapacity) + 1
    );
  }

  function testRevertUpdateFacilitatorBucketCapacityIfStewardLostBucketManagerRole() public {
    (uint256 currentBucketCapacity, ) = GHO_TOKEN.getFacilitatorBucket(address(GHO_ATOKEN));
    GHO_TOKEN.revokeRole(GHO_TOKEN_BUCKET_MANAGER_ROLE, address(GHO_BUCKET_STEWARD));
    vm.expectRevert(
      AccessControlErrorsLib.MISSING_ROLE(
        GHO_TOKEN_BUCKET_MANAGER_ROLE,
        address(GHO_BUCKET_STEWARD)
      )
    );
    vm.prank(RISK_COUNCIL);
    GHO_BUCKET_STEWARD.updateFacilitatorBucketCapacity(
      address(GHO_ATOKEN),
      uint128(currentBucketCapacity) + 1
    );
  }

  function testRevertUpdateFacilitatorBucketCapacityIfMoreThanDouble() public {
    (uint256 currentBucketCapacity, ) = GHO_TOKEN.getFacilitatorBucket(address(GHO_ATOKEN));
    vm.prank(RISK_COUNCIL);
    vm.expectRevert('INVALID_BUCKET_CAPACITY_UPDATE');
    GHO_BUCKET_STEWARD.updateFacilitatorBucketCapacity(
      address(GHO_ATOKEN),
      uint128(currentBucketCapacity * 2) + 1
    );
  }

  function testRevertUpdateFacilitatorBucketCapacityDecrement() public {
    (uint256 currentBucketCapacity, ) = GHO_TOKEN.getFacilitatorBucket(address(GHO_ATOKEN));
    vm.prank(RISK_COUNCIL);
    uint128 newBucketCapacity = uint128(currentBucketCapacity) - 1;
    vm.expectRevert('INVALID_BUCKET_CAPACITY_UPDATE');
    GHO_BUCKET_STEWARD.updateFacilitatorBucketCapacity(address(GHO_ATOKEN), newBucketCapacity);
  }

  function testSetControlledFacilitatorAdd() public {
    address[] memory oldControlledFacilitators = GHO_BUCKET_STEWARD.getControlledFacilitators();
    address[] memory newGsmList = new address[](1);
    newGsmList[0] = address(GHO_GSM_4626);
    vm.prank(SHORT_EXECUTOR);
    GHO_BUCKET_STEWARD.setControlledFacilitator(newGsmList, true);
    address[] memory newControlledFacilitators = GHO_BUCKET_STEWARD.getControlledFacilitators();
    assertEq(newControlledFacilitators.length, oldControlledFacilitators.length + 1);
    assertTrue(_contains(newControlledFacilitators, address(GHO_GSM_4626)));
  }

  function testSetControlledFacilitatorsRemove() public {
    address[] memory oldControlledFacilitators = GHO_BUCKET_STEWARD.getControlledFacilitators();
    address[] memory disableGsmList = new address[](1);
    disableGsmList[0] = address(GHO_GSM);
    vm.prank(SHORT_EXECUTOR);
    GHO_BUCKET_STEWARD.setControlledFacilitator(disableGsmList, false);
    address[] memory newControlledFacilitators = GHO_BUCKET_STEWARD.getControlledFacilitators();
    assertEq(newControlledFacilitators.length, oldControlledFacilitators.length - 1);
    assertFalse(_contains(newControlledFacilitators, address(GHO_GSM)));
  }

  function testRevertSetControlledFacilitatorIfUnauthorized() public {
    vm.expectRevert(OwnableErrorsLib.CALLER_NOT_OWNER());
    vm.prank(RISK_COUNCIL);
    address[] memory newGsmList = new address[](1);
    newGsmList[0] = address(GHO_GSM_4626);
    GHO_BUCKET_STEWARD.setControlledFacilitator(newGsmList, true);
  }
}

src/test/TestGhoCcipSteward.t.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import './TestGhoBase.t.sol';
import {RateLimiter} from '../contracts/misc/dependencies/Ccip.sol';

contract TestGhoCcipSteward is TestGhoBase {
  RateLimiter.Config rateLimitConfig =
    RateLimiter.Config({isEnabled: true, capacity: type(uint128).max, rate: 1e15});
  uint64 remoteChainSelector = 2;

  event ChainConfigured(
    uint64 remoteChainSelector,
    RateLimiter.Config outboundRateLimiterConfig,
    RateLimiter.Config inboundRateLimiterConfig
  );

  function setUp() public {
    // Deploy Gho CCIP Steward
    GHO_CCIP_STEWARD = new GhoCcipSteward(
      address(GHO_TOKEN),
      address(GHO_TOKEN_POOL),
      RISK_COUNCIL,
      true
    );

    /// @dev Since block.timestamp starts at 0 this is a necessary condition (block.timestamp > `MINIMUM_DELAY`) for the timelocked contract methods to work.
    vm.warp(GHO_CCIP_STEWARD.MINIMUM_DELAY() + 1);

    // Grant accesses to the Steward
    vm.startPrank(GHO_TOKEN_POOL.owner());
    GHO_TOKEN_POOL.setRateLimitAdmin(address(GHO_CCIP_STEWARD));
    GHO_TOKEN_POOL.setBridgeLimitAdmin(address(GHO_CCIP_STEWARD));
    vm.stopPrank();
  }

  function testConstructor() public {
    assertEq(GHO_CCIP_STEWARD.MINIMUM_DELAY(), MINIMUM_DELAY_V2);

    assertEq(GHO_CCIP_STEWARD.GHO_TOKEN(), address(GHO_TOKEN));
    assertEq(GHO_CCIP_STEWARD.GHO_TOKEN_POOL(), address(GHO_TOKEN_POOL));
    assertEq(GHO_CCIP_STEWARD.RISK_COUNCIL(), RISK_COUNCIL);
  }

  function testRevertConstructorInvalidGhoToken() public {
    vm.expectRevert('INVALID_GHO_TOKEN');
    new GhoCcipSteward(address(0), address(0x002), address(0x003), true);
  }

  function testRevertConstructorInvalidGhoTokenPool() public {
    vm.expectRevert('INVALID_GHO_TOKEN_POOL');
    new GhoCcipSteward(address(0x001), address(0), address(0x003), true);
  }

  function testRevertConstructorInvalidRiskCouncil() public {
    vm.expectRevert('INVALID_RISK_COUNCIL');
    new GhoCcipSteward(address(0x001), address(0x002), address(0), true);
  }

  function testUpdateBridgeLimit() public {
    uint256 oldBridgeLimit = GHO_TOKEN_POOL.getBridgeLimit();
    uint256 newBridgeLimit = oldBridgeLimit + 1;
    vm.prank(RISK_COUNCIL);
    GHO_CCIP_STEWARD.updateBridgeLimit(newBridgeLimit);
    uint256 currentBridgeLimit = GHO_TOKEN_POOL.getBridgeLimit();
    assertEq(currentBridgeLimit, newBridgeLimit);
  }

  function testRevertUpdateBridgeLimitIfUnauthorized() public {
    uint256 oldBridgeLimit = GHO_TOKEN_POOL.getBridgeLimit();
    uint256 newBridgeLimit = oldBridgeLimit + 1;
    vm.prank(ALICE);
    vm.expectRevert('INVALID_CALLER');
    GHO_CCIP_STEWARD.updateBridgeLimit(newBridgeLimit);
  }

  function testRevertUpdateBridgeLimitIfUpdatedTooSoon() public {
    uint256 oldBridgeLimit = GHO_TOKEN_POOL.getBridgeLimit();
    uint256 newBridgeLimit = oldBridgeLimit + 1;
    vm.prank(RISK_COUNCIL);
    GHO_CCIP_STEWARD.updateBridgeLimit(newBridgeLimit);
    vm.expectRevert('DEBOUNCE_NOT_RESPECTED');
    vm.prank(RISK_COUNCIL);
    GHO_CCIP_STEWARD.updateBridgeLimit(newBridgeLimit);
  }

  function testRevertUpdateBridgeLimitNoChange() public {
    uint256 oldBridgeLimit = GHO_TOKEN_POOL.getBridgeLimit();
    vm.prank(RISK_COUNCIL);
    vm.expectRevert('NO_CHANGE_IN_BRIDGE_LIMIT');
    GHO_CCIP_STEWARD.updateBridgeLimit(oldBridgeLimit);
  }

  function testRevertUpdateBridgeLimitIfDisabled() public {
    // Deploy new Gho CCIP Steward with bridge limit disabled
    GHO_CCIP_STEWARD = new GhoCcipSteward(
      address(GHO_TOKEN),
      address(GHO_TOKEN_POOL),
      RISK_COUNCIL,
      false
    );

    /// @dev Since block.timestamp starts at 0 this is a necessary condition (block.timestamp > `MINIMUM_DELAY`) for the timelocked contract methods to work.
    vm.warp(GHO_CCIP_STEWARD.MINIMUM_DELAY() + 1);

    // Grant accesses to the Steward
    vm.startPrank(GHO_TOKEN_POOL.owner());
    GHO_TOKEN_POOL.setRateLimitAdmin(address(GHO_CCIP_STEWARD));
    GHO_TOKEN_POOL.setBridgeLimitAdmin(address(GHO_CCIP_STEWARD));
    vm.stopPrank();

    uint256 oldBridgeLimit = GHO_TOKEN_POOL.getBridgeLimit();
    uint256 newBridgeLimit = oldBridgeLimit + 1;
    vm.expectRevert('BRIDGE_LIMIT_DISABLED');
    vm.prank(RISK_COUNCIL);
    GHO_CCIP_STEWARD.updateBridgeLimit(newBridgeLimit);
  }

  function testUpdateBridgeLimitTooHigh() public {
    uint256 oldBridgeLimit = GHO_TOKEN_POOL.getBridgeLimit();
    uint256 newBridgeLimit = (oldBridgeLimit + 1) * 2;
    vm.prank(RISK_COUNCIL);
    vm.expectRevert('INVALID_BRIDGE_LIMIT_UPDATE');
    GHO_CCIP_STEWARD.updateBridgeLimit(newBridgeLimit);
  }

  function testUpdateBridgeLimitFuzz(uint256 newBridgeLimit) public {
    uint256 oldBridgeLimit = GHO_TOKEN_POOL.getBridgeLimit();
    newBridgeLimit = bound(newBridgeLimit, 0, oldBridgeLimit * 2);
    vm.prank(RISK_COUNCIL);
    GHO_CCIP_STEWARD.updateBridgeLimit(newBridgeLimit);
    uint256 currentBridgeLimit = GHO_TOKEN_POOL.getBridgeLimit();
    assertEq(currentBridgeLimit, newBridgeLimit);
  }

  function testUpdateRateLimit() public {
    RateLimiter.TokenBucket memory outboundConfig = MockUpgradeableLockReleaseTokenPool(
      GHO_TOKEN_POOL
    ).getCurrentOutboundRateLimiterState(remoteChainSelector);
    RateLimiter.TokenBucket memory inboundConfig = MockUpgradeableLockReleaseTokenPool(
      GHO_TOKEN_POOL
    ).getCurrentInboundRateLimiterState(remoteChainSelector);

    RateLimiter.Config memory newOutboundConfig = RateLimiter.Config({
      isEnabled: true,
      capacity: outboundConfig.capacity + 1,
      rate: outboundConfig.rate + 1
    });

    RateLimiter.Config memory newInboundConfig = RateLimiter.Config({
      isEnabled: true,
      capacity: inboundConfig.capacity + 1,
      rate: inboundConfig.rate + 1
    });

    vm.expectEmit(false, false, false, true);
    emit ChainConfigured(remoteChainSelector, newOutboundConfig, newInboundConfig);
    vm.prank(RISK_COUNCIL);
    GHO_CCIP_STEWARD.updateRateLimit(
      remoteChainSelector,
      newOutboundConfig.isEnabled,
      newOutboundConfig.capacity,
      newOutboundConfig.rate,
      newInboundConfig.isEnabled,
      newInboundConfig.capacity,
      newInboundConfig.rate
    );
  }

  function testRevertUpdateRateLimitIfUnauthorized() public {
    vm.prank(ALICE);
    vm.expectRevert('INVALID_CALLER');
    GHO_CCIP_STEWARD.updateRateLimit(
      remoteChainSelector,
      rateLimitConfig.isEnabled,
      rateLimitConfig.capacity,
      rateLimitConfig.rate,
      rateLimitConfig.isEnabled,
      rateLimitConfig.capacity,
      rateLimitConfig.rate
    );
  }

  function testRevertUpdateRateLimitIfUpdatedTooSoon() public {
    RateLimiter.TokenBucket memory outboundConfig = MockUpgradeableLockReleaseTokenPool(
      GHO_TOKEN_POOL
    ).getCurrentOutboundRateLimiterState(remoteChainSelector);
    RateLimiter.TokenBucket memory inboundConfig = MockUpgradeableLockReleaseTokenPool(
      GHO_TOKEN_POOL
    ).getCurrentInboundRateLimiterState(remoteChainSelector);

    vm.prank(RISK_COUNCIL);
    GHO_CCIP_STEWARD.updateRateLimit(
      remoteChainSelector,
      outboundConfig.isEnabled,
      outboundConfig.capacity + 1,
      outboundConfig.rate,
      inboundConfig.isEnabled,
      inboundConfig.capacity,
      inboundConfig.rate
    );
    vm.expectRevert('DEBOUNCE_NOT_RESPECTED');
    vm.prank(RISK_COUNCIL);
    GHO_CCIP_STEWARD.updateRateLimit(
      remoteChainSelector,
      outboundConfig.isEnabled,
      outboundConfig.capacity + 2,
      outboundConfig.rate,
      inboundConfig.isEnabled,
      inboundConfig.capacity,
      inboundConfig.rate
    );
  }

  function testRevertUpdateRateLimitNoChange() public {
    RateLimiter.TokenBucket memory outboundConfig = MockUpgradeableLockReleaseTokenPool(
      GHO_TOKEN_POOL
    ).getCurrentOutboundRateLimiterState(remoteChainSelector);
    RateLimiter.TokenBucket memory inboundConfig = MockUpgradeableLockReleaseTokenPool(
      GHO_TOKEN_POOL
    ).getCurrentInboundRateLimiterState(remoteChainSelector);

    vm.prank(RISK_COUNCIL);
    vm.expectRevert('NO_CHANGE_IN_RATE_LIMIT');
    GHO_CCIP_STEWARD.updateRateLimit(
      remoteChainSelector,
      outboundConfig.isEnabled,
      outboundConfig.capacity,
      outboundConfig.rate,
      inboundConfig.isEnabled,
      inboundConfig.capacity,
      inboundConfig.rate
    );
  }

  function testRevertUpdateRateLimitToZero() public {
    RateLimiter.Config memory invalidConfig = RateLimiter.Config({
      isEnabled: true,
      capacity: 0,
      rate: 0
    });

    // reverts because capacity or rate cannot be set to 0 when rate limit is enabled
    // this check is enforced on the token pool (see RateLimiter._validateTokenBucketConfig)
    vm.prank(RISK_COUNCIL);
    vm.expectRevert(
      abi.encodeWithSelector(RateLimiter.InvalidRatelimitRate.selector, invalidConfig)
    );
    GHO_CCIP_STEWARD.updateRateLimit(
      remoteChainSelector,
      invalidConfig.isEnabled,
      invalidConfig.capacity,
      invalidConfig.rate,
      invalidConfig.isEnabled,
      invalidConfig.capacity,
      invalidConfig.rate
    );

    invalidConfig.rate = 10;
    vm.prank(RISK_COUNCIL);
    vm.expectRevert(
      abi.encodeWithSelector(RateLimiter.InvalidRatelimitRate.selector, invalidConfig)
    );
    GHO_CCIP_STEWARD.updateRateLimit(
      remoteChainSelector,
      invalidConfig.isEnabled,
      invalidConfig.capacity,
      invalidConfig.rate,
      invalidConfig.isEnabled,
      invalidConfig.capacity,
      invalidConfig.rate
    );
  }

  function testRevertUpdateRateLimitToZeroWhenDisabled() public {
    RateLimiter.Config memory invalidConfig = RateLimiter.Config({
      isEnabled: false,
      capacity: 10,
      rate: 0
    });

    // reverts because capacity and rate both have be set to 0 when rate limit is disabled
    // this check is enforced on the token pool (see RateLimiter._validateTokenBucketConfig)
    vm.prank(RISK_COUNCIL);
    vm.expectRevert(
      abi.encodeWithSelector(RateLimiter.DisabledNonZeroRateLimit.selector, invalidConfig)
    );
    GHO_CCIP_STEWARD.updateRateLimit(
      remoteChainSelector,
      invalidConfig.isEnabled,
      invalidConfig.capacity,
      invalidConfig.rate,
      invalidConfig.isEnabled,
      invalidConfig.capacity,
      invalidConfig.rate
    );
  }

  function testDisableRateLimit() public {
    RateLimiter.TokenBucket memory outboundConfig = MockUpgradeableLockReleaseTokenPool(
      GHO_TOKEN_POOL
    ).getCurrentOutboundRateLimiterState(remoteChainSelector);
    RateLimiter.TokenBucket memory inboundConfig = MockUpgradeableLockReleaseTokenPool(
      GHO_TOKEN_POOL
    ).getCurrentInboundRateLimiterState(remoteChainSelector);

    // assert both inbound & outbound rate limiters are enabled
    assertTrue(outboundConfig.isEnabled);
    assertGt(outboundConfig.capacity, 0);
    assertGt(outboundConfig.rate, 0);

    assertTrue(inboundConfig.isEnabled);
    assertGt(inboundConfig.capacity, 0);
    assertGt(inboundConfig.rate, 0);

    // capacity and rate both have be set to 0 when rate limit is disabled, enforced by token pool
    RateLimiter.Config memory disableLimitConfig = RateLimiter.Config({
      isEnabled: false,
      capacity: 0,
      rate: 0
    });

    // disable both inbound & outbound config
    vm.prank(RISK_COUNCIL);
    GHO_CCIP_STEWARD.updateRateLimit(
      remoteChainSelector,
      disableLimitConfig.isEnabled,
      disableLimitConfig.capacity,
      disableLimitConfig.rate,
      disableLimitConfig.isEnabled,
      disableLimitConfig.capacity,
      disableLimitConfig.rate
    );

    outboundConfig = MockUpgradeableLockReleaseTokenPool(GHO_TOKEN_POOL)
      .getCurrentOutboundRateLimiterState(remoteChainSelector);
    inboundConfig = MockUpgradeableLockReleaseTokenPool(GHO_TOKEN_POOL)
      .getCurrentInboundRateLimiterState(remoteChainSelector);

    assertFalse(outboundConfig.isEnabled);
    assertEq(outboundConfig.capacity, 0);
    assertEq(outboundConfig.rate, 0);

    assertFalse(inboundConfig.isEnabled);
    assertEq(inboundConfig.capacity, 0);
    assertEq(inboundConfig.rate, 0);
  }

  function testChangeEnabledRateLimit() public {
    RateLimiter.TokenBucket memory outboundConfig = MockUpgradeableLockReleaseTokenPool(
      GHO_TOKEN_POOL
    ).getCurrentOutboundRateLimiterState(remoteChainSelector);
    RateLimiter.TokenBucket memory inboundConfig = MockUpgradeableLockReleaseTokenPool(
      GHO_TOKEN_POOL
    ).getCurrentInboundRateLimiterState(remoteChainSelector);

    RateLimiter.Config memory disableLimitConfig = RateLimiter.Config({
      isEnabled: false,
      capacity: 0,
      rate: 0
    });

    // disable both inbound & outbound config
    vm.prank(RISK_COUNCIL);
    GHO_CCIP_STEWARD.updateRateLimit(
      remoteChainSelector,
      disableLimitConfig.isEnabled,
      disableLimitConfig.capacity,
      disableLimitConfig.rate,
      disableLimitConfig.isEnabled,
      disableLimitConfig.capacity,
      disableLimitConfig.rate
    );

    skip(GHO_CCIP_STEWARD.MINIMUM_DELAY() + 1);

    // steward is not allowed to re-enable rate limit
    vm.expectRevert('INVALID_RATE_LIMIT_UPDATE');
    vm.prank(RISK_COUNCIL);
    GHO_CCIP_STEWARD.updateRateLimit(
      remoteChainSelector,
      outboundConfig.isEnabled,
      outboundConfig.capacity,
      outboundConfig.rate,
      inboundConfig.isEnabled,
      inboundConfig.capacity,
      inboundConfig.rate
    );

    // risk admin/DAO can re-enable rate limit on token pool
    vm.prank(GHO_TOKEN_POOL.owner());
    GHO_TOKEN_POOL.setChainRateLimiterConfig(
      remoteChainSelector,
      _castTokenBucketToConfig(outboundConfig),
      _castTokenBucketToConfig(inboundConfig)
    );

    RateLimiter.TokenBucket memory outboundConfigNew = MockUpgradeableLockReleaseTokenPool(
      GHO_TOKEN_POOL
    ).getCurrentOutboundRateLimiterState(remoteChainSelector);
    RateLimiter.TokenBucket memory inboundConfigNew = MockUpgradeableLockReleaseTokenPool(
      GHO_TOKEN_POOL
    ).getCurrentInboundRateLimiterState(remoteChainSelector);

    assertTrue(outboundConfigNew.isEnabled);
    assertEq(outboundConfigNew.capacity, outboundConfig.capacity);
    assertEq(outboundConfigNew.rate, outboundConfig.rate);

    assertTrue(inboundConfigNew.isEnabled);
    assertEq(inboundConfigNew.capacity, inboundConfig.capacity);
    assertEq(inboundConfigNew.rate, inboundConfig.rate);
  }

  function testChangeEnabledRateLimitOnlyOneSide() public {
    RateLimiter.TokenBucket memory outboundConfig = MockUpgradeableLockReleaseTokenPool(
      GHO_TOKEN_POOL
    ).getCurrentOutboundRateLimiterState(remoteChainSelector);
    RateLimiter.TokenBucket memory inboundConfig = MockUpgradeableLockReleaseTokenPool(
      GHO_TOKEN_POOL
    ).getCurrentInboundRateLimiterState(remoteChainSelector);

    assertTrue(outboundConfig.isEnabled);
    assertGt(outboundConfig.capacity, 0);
    assertGt(outboundConfig.rate, 0);

    assertTrue(inboundConfig.isEnabled);
    assertGt(inboundConfig.capacity, 0);
    assertGt(inboundConfig.rate, 0);

    RateLimiter.Config memory disableLimitConfig = RateLimiter.Config({
      isEnabled: false,
      capacity: 0,
      rate: 0
    });

    // disable only outbound config
    vm.prank(RISK_COUNCIL);
    GHO_CCIP_STEWARD.updateRateLimit(
      remoteChainSelector,
      disableLimitConfig.isEnabled,
      disableLimitConfig.capacity,
      disableLimitConfig.rate,
      // preserve inboundConfig
      inboundConfig.isEnabled,
      inboundConfig.capacity,
      inboundConfig.rate
    );

    RateLimiter.TokenBucket memory outboundConfigNew = MockUpgradeableLockReleaseTokenPool(
      GHO_TOKEN_POOL
    ).getCurrentOutboundRateLimiterState(remoteChainSelector);
    RateLimiter.TokenBucket memory inboundConfigNew = MockUpgradeableLockReleaseTokenPool(
      GHO_TOKEN_POOL
    ).getCurrentInboundRateLimiterState(remoteChainSelector);

    assertFalse(outboundConfigNew.isEnabled);
    assertEq(outboundConfigNew.capacity, 0);
    assertEq(outboundConfigNew.rate, 0);

    assertTrue(inboundConfigNew.isEnabled);
    assertEq(inboundConfigNew.capacity, inboundConfig.capacity);
    assertEq(inboundConfigNew.rate, inboundConfig.rate);
  }

  function testRevertUpdateRateLimitRateGreaterThanCapacity() public {
    RateLimiter.Config memory invalidConfig = RateLimiter.Config({
      isEnabled: true,
      capacity: 10,
      rate: 100
    });
    vm.prank(RISK_COUNCIL);
    vm.expectRevert();
    GHO_CCIP_STEWARD.updateRateLimit(
      remoteChainSelector,
      invalidConfig.isEnabled,
      invalidConfig.capacity,
      invalidConfig.rate,
      rateLimitConfig.isEnabled,
      rateLimitConfig.capacity,
      rateLimitConfig.rate
    );
  }

  function testUpdateRateLimitFuzz(
    uint128 outboundCapacity,
    uint128 outboundRate,
    uint128 inboundCapacity,
    uint128 inboundRate
  ) public {
    RateLimiter.TokenBucket memory currentOutboundConfig = MockUpgradeableLockReleaseTokenPool(
      GHO_TOKEN_POOL
    ).getCurrentOutboundRateLimiterState(remoteChainSelector);
    RateLimiter.TokenBucket memory currentInboundConfig = MockUpgradeableLockReleaseTokenPool(
      GHO_TOKEN_POOL
    ).getCurrentInboundRateLimiterState(remoteChainSelector);

    // Capacity must be strictly greater than rate and nothing can change more than 100%
    outboundRate = uint128(bound(outboundRate, 1, currentOutboundConfig.rate * 2));
    outboundCapacity = uint128(
      bound(outboundCapacity, outboundRate + 1, currentOutboundConfig.capacity * 2)
    );
    inboundRate = uint128(bound(inboundRate, 1, currentInboundConfig.rate * 2));
    inboundCapacity = uint128(
      bound(inboundCapacity, inboundRate + 1, currentInboundConfig.capacity * 2)
    );

    vm.prank(RISK_COUNCIL);
    GHO_CCIP_STEWARD.updateRateLimit(
      remoteChainSelector,
      rateLimitConfig.isEnabled,
      outboundCapacity,
      outboundRate,
      rateLimitConfig.isEnabled,
      inboundCapacity,
      inboundRate
    );
  }

  function _castTokenBucketToConfig(
    RateLimiter.TokenBucket memory arg
  ) private view returns (RateLimiter.Config memory) {
    return RateLimiter.Config({isEnabled: arg.isEnabled, capacity: arg.capacity, rate: arg.rate});
  }
}

