     * 1. Alice sellAsset with 1:1 exchangeRate
     * 2. Exchange rate increases, there is an excess of underlying backing GHO
     * 3. Alice buyAsset of the current exposure. There is a mint of GHO before the action so the level is updated.
     */

    (, uint256 ghoLevel) = GHO_TOKEN.getFacilitatorBucket(address(GHO_GSM_4626));
    assertEq(ghoLevel, 0);
    assertEq(USDC_4626_TOKEN.balanceOf(address(GHO_GSM_4626)), 0);
    assertEq(USDC_4626_TOKEN.previewRedeem(USDC_4626_TOKEN.balanceOf(address(GHO_GSM_4626))), 0);
    assertEq(GHO_GSM_4626.getAvailableUnderlyingExposure(), DEFAULT_GSM_USDC_EXPOSURE);
    assertEq(GHO_GSM_4626.getAvailableLiquidity(), 0);

    uint128 sellAssetAmount = DEFAULT_GSM_USDC_AMOUNT;
    uint256 calcGhoMinted = DEFAULT_GSM_GHO_AMOUNT;
    uint256 calcExposure = DEFAULT_GSM_USDC_AMOUNT;
    _sellAsset(GHO_GSM_4626, USDC_4626_TOKEN, USDC_TOKEN, ALICE, sellAssetAmount);

    (, ghoLevel) = GHO_TOKEN.getFacilitatorBucket(address(GHO_GSM_4626));
    assertEq(ghoLevel, calcGhoMinted);
    assertEq(USDC_4626_TOKEN.balanceOf(address(GHO_GSM_4626)), sellAssetAmount);
    assertEq(
      GHO_GSM_4626.getAvailableUnderlyingExposure(),
      DEFAULT_GSM_USDC_EXPOSURE - calcExposure
    );
    assertEq(GHO_GSM_4626.getAvailableLiquidity(), calcExposure);
    assertEq(
      USDC_4626_TOKEN.previewRedeem(USDC_4626_TOKEN.balanceOf(address(GHO_GSM_4626))),
      sellAssetAmount
    );

    // Inflate exchange rate
    _changeExchangeRate(USDC_4626_TOKEN, USDC_TOKEN, sellAssetAmount, true);

    assertEq(ghoLevel, calcGhoMinted);
    assertEq(USDC_4626_TOKEN.balanceOf(address(GHO_GSM_4626)), calcExposure);
    assertEq(
      GHO_GSM_4626.getAvailableUnderlyingExposure(),
      DEFAULT_GSM_USDC_EXPOSURE - calcExposure
    );
    assertEq(GHO_GSM_4626.getAvailableLiquidity(), calcExposure);
    assertEq(
      USDC_4626_TOKEN.previewRedeem(USDC_4626_TOKEN.balanceOf(address(GHO_GSM_4626))),
      sellAssetAmount * 2
    );

    // Top up Alice with GHO
    ghoFaucet(ALICE, 1_000_000e18);

    // Alice buy all assets and there is a mint of GHO backed by excess of underlying before the action
    vm.startPrank(ALICE);
    GHO_TOKEN.approve(address(GHO_GSM_4626), type(uint256).max);

    uint256 totalBackedGho = GHO_GSM_4626_FIXED_PRICE_STRATEGY.getAssetPriceInGho(
      GHO_GSM_4626.getAvailableLiquidity(),
      false
    );
    (, uint256 totalMintedGho) = GHO_TOKEN.getFacilitatorBucket(address(GHO_GSM_4626));
    assertEq(totalBackedGho, totalMintedGho + DEFAULT_GSM_GHO_AMOUNT);

    calcGhoMinted = 0;
    calcExposure = 0;
    GHO_GSM_4626.buyAsset(DEFAULT_GSM_USDC_AMOUNT, ALICE);
    vm.stopPrank();

    (, ghoLevel) = GHO_TOKEN.getFacilitatorBucket(address(GHO_GSM_4626));
    assertEq(ghoLevel, calcGhoMinted);
    assertEq(USDC_4626_TOKEN.balanceOf(address(GHO_GSM_4626)), calcExposure);
    assertEq(
      GHO_GSM_4626.getAvailableUnderlyingExposure(),
      DEFAULT_GSM_USDC_EXPOSURE - calcExposure
    );
    assertEq(GHO_GSM_4626.getAvailableLiquidity(), calcExposure);
    assertEq(USDC_4626_TOKEN.previewRedeem(USDC_4626_TOKEN.balanceOf(address(GHO_GSM_4626))), 0);
  }

  function testBuyAssetAfterLowExchangeRate() public {
    /**
     * 1. Alice sellAsset with 1:1 exchangeRate
     * 2. Exchange rate decreases, there is a portion of GHO unbacked
     * 3. Alice buyAsset of the current exposure
     * 4. Exposure is 0 but level is not 0, so there is unbacked GHO
     */

    (, uint256 ghoLevel) = GHO_TOKEN.getFacilitatorBucket(address(GHO_GSM_4626));
    assertEq(ghoLevel, 0);
    assertEq(USDC_4626_TOKEN.balanceOf(address(GHO_GSM_4626)), 0);
    assertEq(USDC_4626_TOKEN.previewRedeem(USDC_4626_TOKEN.balanceOf(address(GHO_GSM_4626))), 0);
    assertEq(GHO_GSM_4626.getAvailableUnderlyingExposure(), DEFAULT_GSM_USDC_EXPOSURE);
    assertEq(GHO_GSM_4626.getAvailableLiquidity(), 0);

    uint128 sellAssetAmount = DEFAULT_GSM_USDC_AMOUNT;
    uint256 calcGhoMinted = DEFAULT_GSM_GHO_AMOUNT;
    uint256 calcExposure = DEFAULT_GSM_USDC_AMOUNT;
    _sellAsset(GHO_GSM_4626, USDC_4626_TOKEN, USDC_TOKEN, ALICE, sellAssetAmount);

    (, ghoLevel) = GHO_TOKEN.getFacilitatorBucket(address(GHO_GSM_4626));
    assertEq(ghoLevel, calcGhoMinted);
    assertEq(USDC_4626_TOKEN.balanceOf(address(GHO_GSM_4626)), sellAssetAmount);
    assertEq(
      GHO_GSM_4626.getAvailableUnderlyingExposure(),
      DEFAULT_GSM_USDC_EXPOSURE - calcExposure
    );
    assertEq(GHO_GSM_4626.getAvailableLiquidity(), calcExposure);
    assertEq(
      USDC_4626_TOKEN.previewRedeem(USDC_4626_TOKEN.balanceOf(address(GHO_GSM_4626))),
      sellAssetAmount
    );

    // Deflate exchange rate
    _changeExchangeRate(USDC_4626_TOKEN, USDC_TOKEN, DEFAULT_GSM_USDC_AMOUNT / 2, false);

    assertEq(ghoLevel, calcGhoMinted);
    assertEq(USDC_4626_TOKEN.balanceOf(address(GHO_GSM_4626)), calcExposure);
    assertEq(
      GHO_GSM_4626.getAvailableUnderlyingExposure(),
      DEFAULT_GSM_USDC_EXPOSURE - calcExposure
    );
    assertEq(GHO_GSM_4626.getAvailableLiquidity(), calcExposure);
    assertEq(
      USDC_4626_TOKEN.previewRedeem(USDC_4626_TOKEN.balanceOf(address(GHO_GSM_4626))),
      sellAssetAmount / 2
    );

    // Top up Alice with GHO
    ghoFaucet(ALICE, 1_000_000e18);

    // Buy all assets
    vm.startPrank(ALICE);
    calcGhoMinted = DEFAULT_GSM_GHO_AMOUNT / 2;
    calcExposure = 0;
    GHO_TOKEN.approve(address(GHO_GSM_4626), type(uint256).max);
    GHO_GSM_4626.buyAsset(DEFAULT_GSM_USDC_AMOUNT, ALICE);
    vm.stopPrank();

    // 0 exposure, but non-zero level
    (, ghoLevel) = GHO_TOKEN.getFacilitatorBucket(address(GHO_GSM_4626));
    assertTrue(ghoLevel != 0);
    assertEq(ghoLevel, calcGhoMinted);
    assertEq(USDC_4626_TOKEN.balanceOf(address(GHO_GSM_4626)), calcExposure);
    assertEq(
      GHO_GSM_4626.getAvailableUnderlyingExposure(),
      DEFAULT_GSM_USDC_EXPOSURE - calcExposure
    );
    assertEq(GHO_GSM_4626.getAvailableLiquidity(), calcExposure);
    assertEq(USDC_4626_TOKEN.previewRedeem(USDC_4626_TOKEN.balanceOf(address(GHO_GSM_4626))), 0);
  }

  function testBuyAssetAtCapacityWithGain() public {
    /**
     * 1. Alice sellAsset with 1:1 exchangeRate, up to the maximum exposure
     * 2. Exchange rate increases,  there is an excess of underlying backing GHO
     * 3. Alice buyAsset of the maximum exposure, but excess is not minted due to maximum exposure maxed out
     * 4. Excess is minted once a buyAsset occurs and the maximum is not maxed out
     */
    // Use zero fees for easier calculations
    vm.expectEmit(true, true, false, true, address(GHO_GSM_4626));
    emit FeeStrategyUpdated(address(GHO_GSM_FIXED_FEE_STRATEGY), address(0));
    GHO_GSM_4626.updateFeeStrategy(address(0));

    // Supply assets to the GSM first
    _mintVaultAssets(USDC_4626_TOKEN, USDC_TOKEN, ALICE, DEFAULT_GSM_USDC_EXPOSURE);
    vm.startPrank(ALICE);
    USDC_4626_TOKEN.approve(address(GHO_GSM_4626), DEFAULT_GSM_USDC_EXPOSURE);
    vm.expectEmit(true, true, true, true, address(GHO_GSM_4626));
    emit SellAsset(ALICE, ALICE, DEFAULT_GSM_USDC_EXPOSURE, DEFAULT_CAPACITY, 0);
    GHO_GSM_4626.sellAsset(DEFAULT_GSM_USDC_EXPOSURE, ALICE);
    vm.stopPrank();

    (uint256 ghoCapacity, uint256 ghoLevel) = GHO_TOKEN.getFacilitatorBucket(address(GHO_GSM_4626));
    assertEq(ghoLevel, ghoCapacity, 'Unexpected GHO bucket level after initial sell');

    // Simulate a gain
    _changeExchangeRate(USDC_4626_TOKEN, USDC_TOKEN, DEFAULT_GSM_USDC_EXPOSURE / 4, true);
    (uint256 excess, uint256 deficit) = GHO_GSM_4626.getCurrentBacking();
    assertEq(excess, (DEFAULT_GSM_USDC_EXPOSURE / 4) * 1e12, 'Unexpected excess');
    assertEq(deficit, 0, 'Unexpected non-zero deficit');
    uint128 buyAmount = DEFAULT_CAPACITY / (((5 * DEFAULT_GSM_USDC_EXPOSURE) / 4) / 100);

    vm.startPrank(ALICE);
    GHO_TOKEN.approve(address(GHO_GSM_4626), DEFAULT_CAPACITY);
    vm.expectEmit(true, true, true, true, address(GHO_GSM_4626));
    emit BuyAsset(ALICE, ALICE, buyAmount, DEFAULT_CAPACITY, 0);
    GHO_GSM_4626.buyAsset(buyAmount, ALICE);
    vm.stopPrank();

    assertEq(USDC_4626_TOKEN.balanceOf(ALICE), buyAmount, 'Unexpected final USDC balance');
    assertEq(GHO_TOKEN.balanceOf(ALICE), 0, 'Unexpected final GHO balance');

    // Ensure GHO level is at 0, but that excess is unchanged
    (, ghoLevel) = GHO_TOKEN.getFacilitatorBucket(address(GHO_GSM_4626));
    (ghoCapacity, ghoLevel) = GHO_TOKEN.getFacilitatorBucket(address(GHO_GSM_4626));
    assertEq(ghoLevel, 0, 'Unexpected GHO bucket level after initial sell');
    (excess, deficit) = GHO_GSM_4626.getCurrentBacking();
    assertEq(excess, (DEFAULT_GSM_USDC_EXPOSURE / 4) * 1e12, 'Unexpected excess');
    assertEq(deficit, 0, 'Unexpected non-zero deficit');

    // Sell a bit of asset so its possible to buy
    vm.startPrank(ALICE);
    USDC_4626_TOKEN.approve(address(GHO_GSM_4626), 2);
    vm.expectEmit(true, true, true, true, address(GHO_GSM_4626));
    // Expected amount is a result of a 25% gain on 2 of the underlying getting rounded down
    emit SellAsset(ALICE, ALICE, 2, 2e12, 0);
    GHO_GSM_4626.sellAsset(2, ALICE);
    vm.stopPrank();

    // Ensure GHO level is at 2e12, but that excess is unchanged
    (, ghoLevel) = GHO_TOKEN.getFacilitatorBucket(address(GHO_GSM_4626));
    assertEq(ghoLevel, 2e12, 'Unexpected GHO bucket level after initial sell');
    (excess, deficit) = GHO_GSM_4626.getCurrentBacking();
    assertEq(excess, (DEFAULT_GSM_USDC_EXPOSURE / 4) * 1e12, 'Unexpected excess');
    assertEq(deficit, 0, 'Unexpected non-zero deficit');

    // Buy a bit of asset so the excess is minted
    vm.startPrank(ALICE);
    GHO_TOKEN.approve(address(GHO_GSM_4626), 2e12);
    vm.expectEmit(true, true, true, true, address(GHO_GSM_4626));
    emit BuyAsset(ALICE, ALICE, 1, 2e12, 0);
    GHO_GSM_4626.buyAsset(1, ALICE);
    vm.stopPrank();

    // Ensure GHO level is at the previous amount of excess, and excess is now 1e12
    (, ghoLevel) = GHO_TOKEN.getFacilitatorBucket(address(GHO_GSM_4626));
    assertEq(
      ghoLevel,
      (DEFAULT_GSM_USDC_EXPOSURE / 4) * 1e12,
      'Unexpected GHO bucket level after final buy'
    );
    (excess, deficit) = GHO_GSM_4626.getCurrentBacking();
    // Excess of 1e12 due to the last purchase (rounding is causing excess on every sell-buy)
    assertEq(excess, 1e12, 'Unexpected excess');
    assertEq(deficit, 0, 'Unexpected non-zero deficit');
  }

  function testExcessBuildUpDueToUnbalanced4626() public {
    /**
     * 1. Vault gets unbalanced, 1 share equals 1.25 assets
     * 2. Alice sells 2 assets for 2e12 GHO
     * 3. Alice buys 1 asset for 2e12 GHO
     * 4. GSM gets 1 asset due to the imprecision error caused by math and unbalance vault
     */
    // Use zero fees for easier calculations
    vm.expectEmit(true, true, false, true, address(GHO_GSM_4626));
    emit FeeStrategyUpdated(address(GHO_GSM_FIXED_FEE_STRATEGY), address(0));
    GHO_GSM_4626.updateFeeStrategy(address(0));

    // Mint some vault shares first
    _mintVaultAssets(USDC_4626_TOKEN, USDC_TOKEN, ALICE, DEFAULT_GSM_USDC_EXPOSURE);

    // Simulate imbalance in vault (e.g. gift made to the vault, yield accumulation)
    _changeExchangeRate(USDC_4626_TOKEN, USDC_TOKEN, DEFAULT_GSM_USDC_EXPOSURE / 4, true);

    // Sell 2 assets for 2e12 GHO
    vm.startPrank(ALICE);
    USDC_4626_TOKEN.approve(address(GHO_GSM_4626), 2);
    vm.expectEmit(true, true, true, true, address(GHO_GSM_4626));
    // Expected amount is a result of a 25% gain on 2 of the underlying getting rounded down
    emit SellAsset(ALICE, ALICE, 2, 2e12, 0);
    GHO_GSM_4626.sellAsset(2, ALICE);
    vm.stopPrank();

    // Buy 1 asset for 2e12 GHO
    vm.startPrank(ALICE);
    GHO_TOKEN.approve(address(GHO_GSM_4626), 2e12);
    vm.expectEmit(true, true, true, true, address(GHO_GSM_4626));
    emit BuyAsset(ALICE, ALICE, 1, 2e12, 0);
    GHO_GSM_4626.buyAsset(1, ALICE);
    vm.stopPrank();

    (uint256 excess, uint256 deficit) = GHO_GSM_4626.getCurrentBacking();
    // Excess of 1e12 due to the last purchase (rounding is causing excess on every sell-buy)
    assertEq(excess, 1e12, 'Unexpected excess');
    assertEq(deficit, 0, 'Unexpected non-zero deficit');
  }
}

src/test/TestGsmConverter.t.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import './TestGhoBase.t.sol';

contract TestGsmConverter is TestGhoBase {
  // using PercentageMath for uint256;
  // using PercentageMath for uint128;

  address public gsmConverterSignerAddr;
  uint256 public gsmConverterSignerKey;

  function setUp() public {}

  function testConstructor() public {
    GsmConverter gsmConverter = new GsmConverter(
      address(this),
      address(GHO_BUIDL_GSM),
      address(BUIDL_USDC_REDEMPTION),
      address(BUIDL_USDC_ISSUANCE),
      address(BUIDL_TOKEN),
      address(USDC_TOKEN)
    );
    assertEq(gsmConverter.owner(), address(this), 'Unexpected default admin address');
    assertEq(gsmConverter.GSM(), address(GHO_BUIDL_GSM), 'Unexpected GSM address');
    assertEq(
      gsmConverter.REDEMPTION_CONTRACT(),
      address(BUIDL_USDC_REDEMPTION),
      'Unexpected redemption contract address'
    );
    assertEq(
      gsmConverter.SUBSCRIPTION_CONTRACT(),
      address(BUIDL_USDC_ISSUANCE),
      'Unexpected issuance receiver contract address'
    );
    assertEq(gsmConverter.ISSUED_ASSET(), address(BUIDL_TOKEN), 'Unexpected issued asset address');
    assertEq(
      gsmConverter.REDEEMED_ASSET(),
      address(USDC_TOKEN),
      'Unexpected redeemed asset address'
    );
  }

  function testRevertConstructorZeroAddressParams() public {
    vm.expectRevert('ZERO_ADDRESS_NOT_VALID');
    new GsmConverter(
      address(0),
      address(GHO_BUIDL_GSM),
      address(BUIDL_USDC_REDEMPTION),
      address(BUIDL_USDC_ISSUANCE),
      address(BUIDL_TOKEN),
      address(USDC_TOKEN)
    );

    vm.expectRevert('ZERO_ADDRESS_NOT_VALID');
    new GsmConverter(
      address(this),
      address(0),
      address(BUIDL_USDC_REDEMPTION),
      address(BUIDL_USDC_ISSUANCE),
      address(BUIDL_TOKEN),
      address(USDC_TOKEN)
    );

    vm.expectRevert('ZERO_ADDRESS_NOT_VALID');
    new GsmConverter(
      address(this),
      address(GHO_BUIDL_GSM),
      address(0),
      address(BUIDL_USDC_ISSUANCE),
      address(BUIDL_TOKEN),
      address(USDC_TOKEN)
    );

    vm.expectRevert('ZERO_ADDRESS_NOT_VALID');
    new GsmConverter(
      address(this),
      address(GHO_BUIDL_GSM),
      address(BUIDL_USDC_REDEMPTION),
      address(0),
      address(BUIDL_TOKEN),
      address(USDC_TOKEN)
    );

    vm.expectRevert('ZERO_ADDRESS_NOT_VALID');
    new GsmConverter(
      address(this),
      address(GHO_BUIDL_GSM),
      address(BUIDL_USDC_REDEMPTION),
      address(BUIDL_USDC_ISSUANCE),
      address(0),
      address(USDC_TOKEN)
    );

    vm.expectRevert('ZERO_ADDRESS_NOT_VALID');
    new GsmConverter(
      address(this),
      address(GHO_BUIDL_GSM),
      address(BUIDL_USDC_REDEMPTION),
      address(BUIDL_USDC_ISSUANCE),
      address(BUIDL_TOKEN),
      address(0)
    );
  }

  function testSellAsset() public {
    (
      uint256 expectedIssuedAssetAmount,
      uint256 expectedGhoBought,
      ,
      uint256 sellFee
    ) = GHO_BUIDL_GSM.getGhoAmountForSellAsset(DEFAULT_GSM_BUIDL_AMOUNT);

    vm.startPrank(FAUCET);
    // Supply USDC to buyer
    USDC_TOKEN.mint(ALICE, expectedIssuedAssetAmount);
    // Supply BUIDL to issuance contract
    BUIDL_TOKEN.mint(address(BUIDL_USDC_ISSUANCE), expectedIssuedAssetAmount);
    vm.stopPrank();

    vm.startPrank(ALICE);
    USDC_TOKEN.approve(address(GSM_CONVERTER), expectedIssuedAssetAmount);

    vm.expectEmit(true, true, true, true, address(GSM_CONVERTER));
    emit SellAssetThroughSubscription(ALICE, ALICE, expectedIssuedAssetAmount, expectedGhoBought);
    (uint256 assetAmount, uint256 ghoBought) = GSM_CONVERTER.sellAsset(
      DEFAULT_GSM_BUIDL_AMOUNT,
      ALICE
    );
    vm.stopPrank();

    assertEq(ghoBought, expectedGhoBought, 'Unexpected GHO bought amount');
    assertEq(assetAmount, expectedIssuedAssetAmount, 'Unexpected asset amount sold');
    assertEq(USDC_TOKEN.balanceOf(ALICE), 0, 'Unexpected seller final USDC balance');
    assertEq(GHO_TOKEN.balanceOf(ALICE), ghoBought, 'Unexpected seller final GHO balance');
    assertEq(
      BUIDL_TOKEN.balanceOf(ALICE),
      0,
      'Unexpected seller final BUIDL (issued asset) balance'
    );
    assertEq(USDC_TOKEN.balanceOf(address(GHO_BUIDL_GSM)), 0, 'Unexpected GSM final USDC balance');
    assertEq(
      BUIDL_TOKEN.balanceOf(address(GHO_BUIDL_GSM)),
      assetAmount,
      'Unexpected GSM final BUIDL balance'
    );
    assertEq(
      GHO_TOKEN.balanceOf(address(GHO_BUIDL_GSM)),
      sellFee,
      'Unexpected GSM final GHO balance'
    );
    assertEq(
      USDC_TOKEN.balanceOf(address(GSM_CONVERTER)),
      0,
      'Unexpected GSM_CONVERTER final USDC balance'
    );
    assertEq(
      BUIDL_TOKEN.balanceOf(address(GSM_CONVERTER)),
      0,
      'Unexpected GSM_CONVERTER final BUIDL balance'
    );
    assertEq(
      GHO_TOKEN.balanceOf(address(GSM_CONVERTER)),
      0,
      'Unexpected GSM_CONVERTER final GHO balance'
    );
    assertEq(
      BUIDL_TOKEN.balanceOf(address(BUIDL_USDC_ISSUANCE)),
      0,
      'Unexpected Issuance final BUIDL balance'
    );
    assertEq(
      USDC_TOKEN.balanceOf(address(BUIDL_USDC_ISSUANCE)),
      expectedIssuedAssetAmount,
      'Unexpected Issuance final USDC balance'
    );
    assertEq(
      GHO_TOKEN.balanceOf(address(BUIDL_USDC_ISSUANCE)),
      0,
      'Unexpected Issuance final GHO balance'
    );
  }

  function testSellAssetSendToOther() public {
    (
      uint256 expectedIssuedAssetAmount,
      uint256 expectedGhoBought,
      ,
      uint256 sellFee
    ) = GHO_BUIDL_GSM.getGhoAmountForSellAsset(DEFAULT_GSM_BUIDL_AMOUNT);

    vm.startPrank(FAUCET);
    // Supply USDC to buyer
    USDC_TOKEN.mint(ALICE, expectedIssuedAssetAmount);
    // Supply BUIDL to issuance contract
    BUIDL_TOKEN.mint(address(BUIDL_USDC_ISSUANCE), expectedIssuedAssetAmount);
    vm.stopPrank();

    vm.startPrank(ALICE);
    USDC_TOKEN.approve(address(GSM_CONVERTER), expectedIssuedAssetAmount);

    vm.expectEmit(true, true, true, true, address(GSM_CONVERTER));
    emit SellAssetThroughSubscription(ALICE, BOB, expectedIssuedAssetAmount, expectedGhoBought);
    (uint256 assetAmount, uint256 ghoBought) = GSM_CONVERTER.sellAsset(
      DEFAULT_GSM_BUIDL_AMOUNT,
      BOB
    );
    vm.stopPrank();

    assertEq(ghoBought, expectedGhoBought, 'Unexpected GHO bought amount');
    assertEq(assetAmount, expectedIssuedAssetAmount, 'Unexpected asset amount sold');
    assertEq(USDC_TOKEN.balanceOf(BOB), 0, 'Unexpected receiver final USDC balance');
    assertEq(GHO_TOKEN.balanceOf(BOB), ghoBought, 'Unexpected receiver final GHO balance');
    assertEq(
      BUIDL_TOKEN.balanceOf(BOB),
      0,
      'Unexpected receiver final BUIDL (issued asset) balance'
    );
    assertEq(USDC_TOKEN.balanceOf(ALICE), 0, 'Unexpected seller final USDC balance');
    assertEq(GHO_TOKEN.balanceOf(ALICE), 0, 'Unexpected seller final GHO balance');
    assertEq(
      BUIDL_TOKEN.balanceOf(ALICE),
      0,
      'Unexpected seller final BUIDL (issued asset) balance'
    );
    assertEq(USDC_TOKEN.balanceOf(address(GHO_BUIDL_GSM)), 0, 'Unexpected GSM final USDC balance');
    assertEq(
      BUIDL_TOKEN.balanceOf(address(GHO_BUIDL_GSM)),
      assetAmount,
      'Unexpected GSM final BUIDL balance'
    );
    assertEq(
      GHO_TOKEN.balanceOf(address(GHO_BUIDL_GSM)),
      sellFee,
      'Unexpected GSM final GHO balance'
    );
    assertEq(
      USDC_TOKEN.balanceOf(address(GSM_CONVERTER)),
      0,
      'Unexpected GSM_CONVERTER final USDC balance'
    );
    assertEq(
      BUIDL_TOKEN.balanceOf(address(GSM_CONVERTER)),
      0,
      'Unexpected GSM_CONVERTER final BUIDL balance'
    );
    assertEq(
      GHO_TOKEN.balanceOf(address(GSM_CONVERTER)),
      0,
      'Unexpected GSM_CONVERTER final GHO balance'
    );
    assertEq(
      BUIDL_TOKEN.balanceOf(address(BUIDL_USDC_ISSUANCE)),
      0,
      'Unexpected Issuance final BUIDL balance'
    );
    assertEq(
      USDC_TOKEN.balanceOf(address(BUIDL_USDC_ISSUANCE)),
      expectedIssuedAssetAmount,
      'Unexpected Issuance final USDC balance'
    );
    assertEq(
      GHO_TOKEN.balanceOf(address(BUIDL_USDC_ISSUANCE)),
      0,
      'Unexpected Issuance final GHO balance'
    );
  }

  function testSellAssetDonatedTokens() public {
    uint256 sellFee = GHO_GSM_FIXED_FEE_STRATEGY.getSellFee(DEFAULT_GSM_GHO_AMOUNT);
    (uint256 expectedIssuedAssetAmount, uint256 expectedGhoBought, , ) = GHO_BUIDL_GSM
      .getGhoAmountForSellAsset(DEFAULT_GSM_BUIDL_AMOUNT);

    uint256 donatedAmount = 1e6;

    vm.startPrank(FAUCET);
    // Supply USDC to buyer
    USDC_TOKEN.mint(ALICE, expectedIssuedAssetAmount);
    // Supply BUIDL to issuance contract
    BUIDL_TOKEN.mint(address(BUIDL_USDC_ISSUANCE), expectedIssuedAssetAmount);
    // donate tokens to the converter
    BUIDL_TOKEN.mint(address(GSM_CONVERTER), donatedAmount);
    USDC_TOKEN.mint(address(GSM_CONVERTER), donatedAmount);
